// Get raw output from the AI node
let raw = $json.output || $json.text || "";

// Ensure raw is a string
if (typeof raw !== "string") {
  raw = JSON.stringify(raw);
}

// Clean markdown fences
let cleaned = raw.replace(/```json|```/g, "").trim();

let result = {
  subject: "Untitled Newsletter",
  content: ""
};

try {
  // Try parsing full JSON
  const parsed = JSON.parse(cleaned);

  if (parsed.subject && typeof parsed.subject === "string") {
    result.subject = parsed.subject.trim();
  }

  if (parsed.content && typeof parsed.content === "string") {
    result.content = parsed.content;
  }
} catch (e) {
  // Regex fallback
  const subjectMatch = cleaned.match(/"subject"\s*:\s*"([^"]+)"/);
  const contentMatch = cleaned.match(/"content"\s*:\s*"([\s\S]+)"/);

  if (subjectMatch) {
    result.subject = subjectMatch[1];
  }

  if (contentMatch) {
    result.content = contentMatch[1];
  } else {
    result.content = cleaned;
  }
}

// Last-resort subject: derive from content
if (!result.subject || result.subject === "Untitled Newsletter") {
  if (result.content) {
    // Try first <h3>
    const h3Match = result.content.match(/<h3[^>]*>(.*?)<\/h3>/i);
    if (h3Match) {
      result.subject = h3Match[1].trim();
    } else {
      // Otherwise use first 80 chars of content
      result.subject = result.content.replace(/<[^>]*>/g, "").slice(0, 77) + "...";
    }
  }
}

// Enforce subject â‰¤ 80 chars
if (result.subject.length > 80) {
  result.subject = result.subject.substring(0, 77) + "...";
}

return [result];
